import React, { useState, useRef } from 'react';
import { MapPin, Phone, Mail, Calendar, Clock, Users, Video, Star, Palette, Check, Search } from 'lucide-react';

// Multiple theme configurations
const themes = {
  ocean: {
    name: 'Ocean Blue',
    primary: 'from-blue-600 to-blue-700',
    primarySolid: 'bg-blue-600',
    primaryHover: 'hover:bg-blue-700',
    accent: 'text-blue-600',
    accentBg: 'bg-blue-50',
    border: 'border-blue-200',
    text: 'text-blue-800',
    gradient: 'from-blue-400 to-blue-600'
  },
  sunset: {
    name: 'Sunset Orange',
    primary: 'from-orange-500 to-red-500',
    primarySolid: 'bg-orange-500',
    primaryHover: 'hover:bg-orange-600',
    accent: 'text-orange-600',
    accentBg: 'bg-orange-50',
    border: 'border-orange-200',
    text: 'text-orange-800',
    gradient: 'from-orange-400 to-red-500'
  },
  forest: {
    name: 'Forest Green',
    primary: 'from-emerald-600 to-green-700',
    primarySolid: 'bg-emerald-600',
    primaryHover: 'hover:bg-emerald-700',
    accent: 'text-emerald-600',
    accentBg: 'bg-emerald-50',
    border: 'border-emerald-200',
    text: 'text-emerald-800',
    gradient: 'from-emerald-400 to-green-600'
  },
  lavender: {
    name: 'Lavender Purple',
    primary: 'from-purple-600 to-violet-700',
    primarySolid: 'bg-purple-600',
    primaryHover: 'hover:bg-purple-700',
    accent: 'text-purple-600',
    accentBg: 'bg-purple-50',
    border: 'border-purple-200',
    text: 'text-purple-800',
    gradient: 'from-purple-400 to-violet-600'
  },
  rose: {
    name: 'Rose Pink',
    primary: 'from-pink-500 to-rose-600',
    primarySolid: 'bg-pink-500',
    primaryHover: 'hover:bg-pink-600',
    accent: 'text-pink-600',
    accentBg: 'bg-pink-50',
    border: 'border-pink-200',
    text: 'text-pink-800',
    gradient: 'from-pink-400 to-rose-500'
  },
  midnight: {
    name: 'Midnight Dark',
    primary: 'from-slate-700 to-slate-900',
    primarySolid: 'bg-slate-700',
    primaryHover: 'hover:bg-slate-800',
    accent: 'text-slate-700',
    accentBg: 'bg-slate-50',
    border: 'border-slate-200',
    text: 'text-slate-800',
    gradient: 'from-slate-500 to-slate-700'
  },
  teal: {
    name: 'Teal Wave',
    primary: 'from-teal-600 to-cyan-700',
    primarySolid: 'bg-teal-600',
    primaryHover: 'hover:bg-teal-700',
    accent: 'text-teal-600',
    accentBg: 'bg-teal-50',
    border: 'border-teal-200',
    text: 'text-teal-800',
    gradient: 'from-teal-400 to-cyan-600'
  },
  amber: {
    name: 'Golden Amber',
    primary: 'from-amber-500 to-yellow-600',
    primarySolid: 'bg-amber-500',
    primaryHover: 'hover:bg-amber-600',
    accent: 'text-amber-600',
    accentBg: 'bg-amber-50',
    border: 'border-amber-200',
    text: 'text-amber-800',
    gradient: 'from-amber-400 to-yellow-500'
  }
};

export default function CareHQBooking() {
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [currentTheme, setCurrentTheme] = useState('ocean');
  const [showThemeSelector, setShowThemeSelector] = useState(false);
  const [formData, setFormData] = useState({
    fullName: '',
    dateOfBirth: '',
    phoneNumber: '',
    email: '',
    city: '',
    postcode: '',
    symptoms: '',
    // GMS Number field
    gmsNumber: '',
    // GP and Surgery fields
    gp: '',
    surgery: '',
    // GMS Expiry field
    gmsExpiry: '',
    // Eircode field
    eircode: '',
    // Home Location fields
    homeBuilding: '',
    homeStreet: '',
    homeCity: '',
    homeCountry: '',
    homePostcode: '',
    // Current Location fields
    currentBuilding: '',
    currentStreet: '',
    currentCity: '',
    currentCountry: '',
    currentPostcode: '',
    // Payment fields
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardName: '',
    billingAddress: ''
  });
  const [useHomeAsCurrentLocation, setUseHomeAsCurrentLocation] = useState(false);
  const [isLoadingEircode, setIsLoadingEircode] = useState(false);
  const [eircodeError, setEircodeError] = useState('');
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);
  const [bookingReference, setBookingReference] = useState('');

  const theme = themes[currentTheme];

  const clinics = [
    {
      id: 1,
      name: 'CareHQ Medical Centre',
      distance: '2.5 km',
      price: '£45',
      rating: 4.8,
      slots: ['17:00 - 17:15', '17:15 - 17:30', '17:30 - 17:45', '17:45 - 18:00', '18:00 - 18:15', '18:15 - 18:30',
        '18:30 - 18:45', '18:45 - 19:00', '19:00 - 19:15', '19:15 - 19:30', '19:30 - 19:45', '19:45 - 20:00',
        '20:00 - 20:15', '20:15 - 20:30', '20:30 - 20:45', '20:45 - 21:00', '21:00 - 21:15', '21:15 - 21:30',
        '21:30 - 21:45', '21:45 - 22:00'
      ]
    },
    {
      id: 2,
      name: 'Downtown Health Clinic',
      distance: '3.8 km',
      price: '£40',
      rating: 4.6,
      slots: ['17:00 - 17:15', '17:15 - 17:30', '17:30 - 17:45', '17:45 - 18:00', '18:00 - 18:15', '18:15 - 18:30',
        '18:30 - 18:45', '18:45 - 19:00', '19:00 - 19:15', '19:15 - 19:30', '19:30 - 19:45', '19:45 - 20:00',
        '20:00 - 20:15', '20:15 - 20:30', '20:30 - 20:45', '20:45 - 21:00', '21:00 - 21:15', '21:15 - 21:30'
      ]
    },
    {
      id: 3,
      name: 'City Care Medical',
      distance: '1.2 km',
      price: '£50',
      rating: 4.9,
      slots: ['17:00 - 17:15', '17:15 - 17:30', '17:30 - 17:45', '17:45 - 18:00', '18:00 - 18:15', '18:15 - 18:30',
        '18:30 - 18:45', '18:45 - 19:00', '19:00 - 19:15', '19:15 - 19:30', '19:30 - 19:45', '19:45 - 20:00',
        '20:00 - 20:15', '20:15 - 20:30', '20:30 - 20:45', '20:45 - 21:00', '21:00 - 21:15', '21:30 - 21:45',
        '21:45 - 22:00'
      ]
    },
    {
      id: 4,
      name: 'Wellness Center Plus',
      distance: '4.1 km',
      price: '£42',
      rating: 4.7,
      slots: ['17:00 - 17:15', '17:15 - 17:30', '17:30 - 17:45', '17:45 - 18:00', '18:00 - 18:15', '18:15 - 18:30',
        '18:30 - 18:45', '18:45 - 19:00', '19:00 - 19:15', '19:15 - 19:30', '19:30 - 19:45', '19:45 - 20:00',
        '20:00 - 20:15', '20:15 - 20:30', '20:30 - 20:45', '20:45 - 21:00', '21:00 - 21:15', '21:15 - 21:30',
        '21:30 - 21:45'
      ]
    },
    {
      id: 5,
      name: 'Prime Health Clinic',
      distance: '2.8 km',
      price: '£48',
      rating: 4.8,
      slots: ['17:00 - 17:15', '17:15 - 17:30', '17:30 - 17:45', '17:45 - 18:00', '18:00 - 18:15', '18:15 - 18:30',
        '18:30 - 18:45', '18:45 - 19:00', '19:00 - 19:15', '19:15 - 19:30', '19:30 - 19:45', '19:45 - 20:00',
        '20:00 - 20:15', '20:15 - 20:30', '20:30 - 20:45', '20:45 - 21:00', '21:00 - 21:15', '21:15 - 21:30',
        '21:30 - 21:45', '21:45 - 22:00'
      ]
    }
  ];

  // Debounce timer ref
  const eircodeTimeoutRef = useRef(null);

  // Eircode sanitization function
  const sanitizeEircode = (eircode) => {
    if (!eircode) return '';
    // Remove all spaces and convert to uppercase
    return eircode.replace(/\s+/g, '').toUpperCase();
  };

  // Validate Eircode format (Irish format: 3 chars + 4 chars)
  const isValidEircodeFormat = (eircode) => {
    const sanitized = sanitizeEircode(eircode);
    // Irish Eircode format: 3 alphanumeric + 4 alphanumeric (e.g., D02XY45)
    const eircodeRegex = /^[A-Z0-9]{3}[A-Z0-9]{4}$/;
    return eircodeRegex.test(sanitized);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const newFormData = {
      ...formData,
      [name]: value
    };

    // Handle GP selection - automatically select corresponding practice
    if (name === 'gp' && value) {
      // Map GP values to corresponding surgery values
      // Since both dropdowns use the same values (ftf, vc, pc), we can directly map them
      newFormData.surgery = value;
    }

    // If checkbox is checked and a home location field is being updated,
    // also update the corresponding current location field
    if (useHomeAsCurrentLocation) {
      if (name === 'homeBuilding') newFormData.currentBuilding = value;
      else if (name === 'homeStreet') newFormData.currentStreet = value;
      else if (name === 'homeCity') newFormData.currentCity = value;
      else if (name === 'homeCountry') newFormData.currentCountry = value;
      else if (name === 'homePostcode') newFormData.currentPostcode = value;
    }

    setFormData(newFormData);

    // Trigger Eircode lookup when Eircode field changes
    if (name === 'eircode') {
      // Clear previous timeout
      if (eircodeTimeoutRef.current) {
        clearTimeout(eircodeTimeoutRef.current);
      }

      // Clear error when user starts typing
      setEircodeError('');

      const sanitizedEircode = sanitizeEircode(value);

      if (value.length === 0) {
        // Clear current location fields when Eircode is cleared
        setFormData(prev => ({
          ...prev,
          currentBuilding: '',
          currentStreet: '',
          currentCity: '',
          currentCountry: '',
          currentPostcode: ''
        }));
      } else if (sanitizedEircode.length >= 6) {
        // Debounce the API call - only trigger when we have enough characters
        eircodeTimeoutRef.current = setTimeout(() => {
          // Get the current value from the form data to ensure we're using the latest value
          const currentEircode = sanitizeEircode(newFormData.eircode);
          if (currentEircode.length >= 6) {
            lookupEircode(currentEircode);
          }
        }, 1000);
      }
    }
  };

  const handleClinicSelect = (clinic) => {
    if (selectedClinic?.id === clinic.id) {
      setSelectedClinic(null);
      setSelectedSlot(null);
    } else {
      setSelectedClinic(clinic);
      setSelectedSlot(null);
    }
  };

  const handleSlotSelect = (slot) => {
    setSelectedSlot(slot);
  };
  const handleThemeChange = (themeKey) => {
    setCurrentTheme(themeKey);
    setShowThemeSelector(false);
  };

  const handleUseHomeAsCurrentLocation = (e) => {
    const isChecked = e.target.checked;
    setUseHomeAsCurrentLocation(isChecked);

    if (isChecked) {
      // Copy home location fields to current location fields
      setFormData(prev => ({
        ...prev,
        currentBuilding: prev.homeBuilding,
        currentStreet: prev.homeStreet,
        currentCity: prev.homeCity,
        currentCountry: prev.homeCountry,
        currentPostcode: prev.homePostcode
      }));
    }
  };

  // Helper function to check if appointment type requires clinic selection
  const isVirtualAppointment = () => {
    return formData.appointmenttype === 'vc' || formData.appointmenttype === 'pc';
  };

  // Generate random booking reference number
  const generateBookingReference = () => {
    const prefix = 'BK';
    const timestamp = Date.now().toString().slice(-6);
    const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
    return `${prefix}${timestamp}${random}`;
  };

  // Handle booking completion
  const handleCompleteBooking = () => {
    const reference = generateBookingReference();
    setBookingReference(reference);
    setShowSuccessPopup(true);
  };

  // Handle success popup close
  const handleSuccessPopupClose = () => {
    setShowSuccessPopup(false);
    setCurrentStep(1);
    // Reset form data if needed
    setSelectedClinic(null);
    setSelectedSlot(null);
  };

  // Get available time slots based on appointment type
  const getAvailableTimeSlots = () => {
    if (isVirtualAppointment()) {
      // For virtual appointments, return evening time slots in 24-hour format
      return [
        '17:00 - 17:15', '17:15 - 17:30', '17:30 - 17:45', '17:45 - 18:00',
        '18:00 - 18:15', '18:15 - 18:30', '18:30 - 18:45', '18:45 - 19:00',
        '19:00 - 19:15', '19:15 - 19:30', '19:30 - 19:45', '19:45 - 20:00',
        '20:00 - 20:15', '20:15 - 20:30', '20:30 - 20:45', '20:45 - 21:00',
        '21:00 - 21:15', '21:15 - 21:30', '21:30 - 21:45', '21:45 - 22:00'
      ];
    } else {
      // For face-to-face appointments, return clinic-specific slots
      return selectedClinic ? selectedClinic.slots : [];
    }
  };

  // Get appointment type display name
  const getAppointmentTypeDisplay = () => {
    switch (formData.appointmenttype) {
      case 'vc': return 'Video Consultation';
      case 'pc': return 'Phone Consultation';
      case 'ftf': return 'Face-to-Face Appointment';
      default: return 'Appointment';
    }
  };

  // Google Maps Geocoding API function with fallback
  const lookupEircode = async (eircode) => {
    const sanitizedEircode = sanitizeEircode(eircode);

    if (!sanitizedEircode || sanitizedEircode.length < 6) {
      setEircodeError('Please enter a valid Eircode (e.g., D02 XY45)');
      return;
    }

    if (!isValidEircodeFormat(sanitizedEircode)) {
      setEircodeError('Invalid Eircode format. Irish Eircodes are 7 characters (e.g., D02XY45)');
      return;
    }

    setIsLoadingEircode(true);
    setEircodeError('');

    try {
      // First, try Google Maps Geocoding API
      const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

      let addressData = null;

      if (GOOGLE_MAPS_API_KEY) {
        try {
          const response = await fetch(
            `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(sanitizedEircode + ', Ireland')}&key=${GOOGLE_MAPS_API_KEY}`
          );

          if (response.ok) {
            const data = await response.json();

            if (data.status === 'OK' && data.results && data.results.length > 0) {
              const result = data.results[0];
              const addressComponents = result.address_components;

              // Extract address components
              const streetNumber = addressComponents.find(comp =>
                comp.types.includes('street_number'))?.long_name || '';
              const route = addressComponents.find(comp =>
                comp.types.includes('route'))?.long_name || '';
              const locality = addressComponents.find(comp =>
                comp.types.includes('locality') || comp.types.includes('administrative_area_level_2'))?.long_name || '';
              const country = addressComponents.find(comp =>
                comp.types.includes('country'))?.long_name || 'Ireland';

              addressData = {
                building: streetNumber,
                street: route,
                city: locality,
                country: country
              };
            }
          }
        } catch (apiError) {
          console.warn('Google Maps API error, falling back to mock data:', apiError);
        }
      }

      // Fallback to comprehensive mock database if Google Maps API fails or is not available
      if (!addressData) {
        const mockEircodeDatabase = {
          // Dublin Eircodes
          'D01F5P2': { building: '42', street: 'O\'Connell Street', city: 'Dublin 1', country: 'Ireland' },
          'D02XY45': { building: '15', street: 'Grafton Street', city: 'Dublin 2', country: 'Ireland' },
          'D02VN51': { building: '88', street: 'Stephen\'s Green', city: 'Dublin 2', country: 'Ireland' },
          'D03K7X2': { building: '23', street: 'Mountjoy Square', city: 'Dublin 3', country: 'Ireland' },
          'D04E6W3': { building: '8', street: 'Baggot Street', city: 'Dublin 4', country: 'Ireland' },
          'D04V2N8': { building: '156', street: 'Pembroke Road', city: 'Dublin 4', country: 'Ireland' },
          'D06F2H4': { building: '67', street: 'Rathmines Road', city: 'Dublin 6', country: 'Ireland' },
          'D07X4C9': { building: '34', street: 'Cabra Road', city: 'Dublin 7', country: 'Ireland' },
          'D08Y7R1': { building: '91', street: 'South Circular Road', city: 'Dublin 8', country: 'Ireland' },
          'D09K2P5': { building: '12', street: 'Glasnevin Avenue', city: 'Dublin 9', country: 'Ireland' },
          'D11A8F3': { building: '45', street: 'Finglas Road', city: 'Dublin 11', country: 'Ireland' },
          'D12N6Y7': { building: '78', street: 'Terenure Road', city: 'Dublin 12', country: 'Ireland' },
          'D14C9K1': { building: '29', street: 'Dundrum Road', city: 'Dublin 14', country: 'Ireland' },
          'D15H3M8': { building: '103', street: 'Blanchardstown Road', city: 'Dublin 15', country: 'Ireland' },
          'D18E5T2': { building: '56', street: 'Sandyford Road', city: 'Dublin 18', country: 'Ireland' },
          'D20F7K9': { building: '87', street: 'Palmerstown Road', city: 'Dublin 20', country: 'Ireland' },
          'D22R4W6': { building: '14', street: 'Clondalkin Road', city: 'Dublin 22', country: 'Ireland' },
          'D24P8N3': { building: '39', street: 'Tallaght Road', city: 'Dublin 24', country: 'Ireland' },

          // Cork Eircodes
          'T12AB34': { building: '25', street: 'Patrick Street', city: 'Cork', country: 'Ireland' },
          'T12K8R7': { building: '63', street: 'Grand Parade', city: 'Cork', country: 'Ireland' },
          'T23F4N1': { building: '18', street: 'Washington Street', city: 'Cork', country: 'Ireland' },
          'T23Y9P5': { building: '92', street: 'MacCurtain Street', city: 'Cork', country: 'Ireland' },

          // Galway Eircodes
          'H91CD56': { building: '12', street: 'Shop Street', city: 'Galway', country: 'Ireland' },
          'H91W2X8': { building: '47', street: 'Quay Street', city: 'Galway', country: 'Ireland' },
          'H91K5M3': { building: '71', street: 'Eyre Square', city: 'Galway', country: 'Ireland' },

          // Limerick Eircodes
          'V94EF78': { building: '33', street: 'O\'Connell Street', city: 'Limerick', country: 'Ireland' },
          'V94T6Y2': { building: '85', street: 'Henry Street', city: 'Limerick', country: 'Ireland' },

          // Waterford Eircodes
          'X91P7K4': { building: '26', street: 'The Quay', city: 'Waterford', country: 'Ireland' },
          'X91H9F1': { building: '54', street: 'Barronstrand Street', city: 'Waterford', country: 'Ireland' },

          // Kilkenny Eircodes
          'R95GH90': { building: '7', street: 'Castle Street', city: 'Kilkenny', country: 'Ireland' },
          'R95X3C6': { building: '41', street: 'High Street', city: 'Kilkenny', country: 'Ireland' },

          // Other locations
          'A94IJ12': { building: '19', street: 'Main Street', city: 'Dundalk', country: 'Ireland' },
          'F92K7P3': { building: '62', street: 'Abbey Street', city: 'Ennis', country: 'Ireland' },
          'N37W8Y5': { building: '35', street: 'William Street', city: 'Listowel', country: 'Ireland' },
          'P85R2M9': { building: '73', street: 'Bridge Street', city: 'Skibbereen', country: 'Ireland' },
          'Y35F6K1': { building: '28', street: 'Main Street', city: 'Gorey', country: 'Ireland' },
          'C15N4T7': { building: '96', street: 'Oliver Plunkett Street', city: 'Monaghan', country: 'Ireland' },
          'F23H8W2': { building: '17', street: 'Castle Street', city: 'Sligo', country: 'Ireland' },
          'F94P5R8': { building: '83', street: 'Shop Street', city: 'Tuam', country: 'Ireland' }
        };

        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 800));
        addressData = mockEircodeDatabase[sanitizedEircode];
      }

      if (addressData) {
        // Update current location fields with the geocoded data
        setFormData(prev => ({
          ...prev,
          currentBuilding: addressData.building || '',
          currentStreet: addressData.street || '',
          currentCity: addressData.city || '',
          currentCountry: addressData.country || 'Ireland',
          currentPostcode: sanitizedEircode
        }));

        setEircodeError('');
      } else {
        setEircodeError(`Eircode "${sanitizedEircode}" not found. Please verify the Eircode and try again.`);
      }
    } catch (error) {
      console.error('Error looking up Eircode:', error);
      setEircodeError('Error looking up address. Please check your internet connection and try again.');
    } finally {
      setIsLoadingEircode(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 relative">
      {/* Theme Selector Button */}
      <div className="fixed top-4 right-4 z-50">
        <div className="relative">
          <button
            onClick={() => setShowThemeSelector(!showThemeSelector)}
            className={`p-3 rounded-full ${theme.primarySolid} ${theme.primaryHover} text-white shadow-lg transition-all transform hover:scale-105`}
          >
            <Palette size={20} />
          </button>

          {/* Theme Selector Dropdown */}
          {showThemeSelector && (
            <div className="absolute right-0 mt-2 w-64 bg-white rounded-2xl shadow-2xl border border-gray-100 overflow-hidden animate-in fade-in slide-in-from-top-2 duration-200">
              <div className="p-4 border-b border-gray-100">
                <h3 className="font-semibold text-gray-900">Choose Theme</h3>
                <p className="text-sm text-gray-600">Customize your experience</p>
              </div>
              <div className="p-2 max-h-80 overflow-y-auto">
                {Object.entries(themes).map(([key, themeOption]) => (
                  <button
                    key={key}
                    onClick={() => handleThemeChange(key)}
                    className={`w-full p-3 rounded-lg mb-2 text-left transition-all hover:bg-gray-50 ${currentTheme === key ? 'bg-gray-100 ring-2 ring-offset-2 ring-gray-200' : ''
                      }`}
                  >
                    <div className="flex items-center space-x-3">
                      <div className={`w-8 h-8 rounded-lg bg-gradient-to-r ${themeOption.gradient} shadow-sm`}></div>
                      <div>
                        <div className="font-medium text-gray-900">{themeOption.name}</div>
                        {currentTheme === key && (
                          <div className="flex items-center space-x-1 text-sm text-gray-600">
                            <Check size={12} />
                            <span>Active</span>
                          </div>
                        )}
                      </div>
                    </div>
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
      {/* Header */}
      <header className={`bg-gradient-to-r ${theme.primary} shadow-lg`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">

            {/* <div className="flex items-center">
