import React, { useState, useRef, useEffect } from 'react';
import { MapPin, Phone, Users, Video, Star, Palette, Check, Search } from 'lucide-react';
import { WEBHOOK_CONFIG, createWebhookRequestBody, makeWebhookRequest } from './config/webhooks';

// Multiple theme configurations
const themes = {
  ocean: {
    name: 'Ocean Blue',
    primary: 'from-blue-600 to-blue-700',
    primarySolid: 'bg-blue-600',
    primaryHover: 'hover:bg-blue-700',
    accent: 'text-blue-600',
    accentBg: 'bg-blue-50',
    border: 'border-blue-200',
    text: 'text-blue-800',
    gradient: 'from-blue-400 to-blue-600'
  },
  sunset: {
    name: 'Sunset Orange',
    primary: 'from-orange-500 to-red-500',
    primarySolid: 'bg-orange-500',
    primaryHover: 'hover:bg-orange-600',
    accent: 'text-orange-600',
    accentBg: 'bg-orange-50',
    border: 'border-orange-200',
    text: 'text-orange-800',
    gradient: 'from-orange-400 to-red-500'
  },
  forest: {
    name: 'Forest Green',
    primary: 'from-emerald-600 to-green-700',
    primarySolid: 'bg-emerald-600',
    primaryHover: 'hover:bg-emerald-700',
    accent: 'text-emerald-600',
    accentBg: 'bg-emerald-50',
    border: 'border-emerald-200',
    text: 'text-emerald-800',
    gradient: 'from-emerald-400 to-green-600'
  },
  lavender: {
    name: 'Lavender Purple',
    primary: 'from-purple-600 to-violet-700',
    primarySolid: 'bg-purple-600',
    primaryHover: 'hover:bg-purple-700',
    accent: 'text-purple-600',
    accentBg: 'bg-purple-50',
    border: 'border-purple-200',
    text: 'text-purple-800',
    gradient: 'from-purple-400 to-violet-600'
  },
  rose: {
    name: 'Rose Pink',
    primary: 'from-pink-500 to-rose-600',
    primarySolid: 'bg-pink-500',
    primaryHover: 'hover:bg-pink-600',
    accent: 'text-pink-600',
    accentBg: 'bg-pink-50',
    border: 'border-pink-200',
    text: 'text-pink-800',
    gradient: 'from-pink-400 to-rose-500'
  },
  midnight: {
    name: 'Midnight Dark',
    primary: 'from-slate-700 to-slate-900',
    primarySolid: 'bg-slate-700',
    primaryHover: 'hover:bg-slate-800',
    accent: 'text-slate-700',
    accentBg: 'bg-slate-50',
    border: 'border-slate-200',
    text: 'text-slate-800',
    gradient: 'from-slate-500 to-slate-700'
  },
  teal: {
    name: 'Teal Wave',
    primary: 'from-teal-600 to-cyan-700',
    primarySolid: 'bg-teal-600',
    primaryHover: 'hover:bg-teal-700',
    accent: 'text-teal-600',
    accentBg: 'bg-teal-50',
    border: 'border-teal-200',
    text: 'text-teal-800',
    gradient: 'from-teal-400 to-cyan-600'
  },
  amber: {
    name: 'Golden Amber',
    primary: 'from-amber-500 to-yellow-600',
    primarySolid: 'bg-amber-500',
    primaryHover: 'hover:bg-amber-600',
    accent: 'text-amber-600',
    accentBg: 'bg-amber-50',
    border: 'border-amber-200',
    text: 'text-amber-800',
    gradient: 'from-amber-400 to-yellow-500'
  }
};

export default function CareHQBooking() {
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedClinic, setSelectedClinic] = useState(null);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [currentTheme, setCurrentTheme] = useState(() => {
    // Load theme from localStorage or default to 'ocean'
    const savedTheme = localStorage.getItem('careHQTheme');
    return savedTheme && themes[savedTheme] ? savedTheme : 'ocean';
  });
  const [showThemeSelector, setShowThemeSelector] = useState(false);
  const [formData, setFormData] = useState({
    reasonForContact: '',
    firstName: '',
    lastName: '',
    fullName: '',
    dateOfBirth: '',
    phoneNumber: '',
    email: '',
    city: '',
    postcode: '',
    symptoms: '',
    // GMS Number field
    gmsNumber: '',
    // GP and Surgery fields
    gp: '',
    surgery: '',
    // GMS Expiry field
    gmsExpiry: '',
    // Appointment Type field
    appointmentType: '',
    // Eircode field
    eircode: '',
    // Home Location fields
    homeBuilding: '',
    homeStreet: '',
    homeCity: '',
    homeCountry: '',
    homePostcode: '',
    homeEircode: '',
    // Current Location fields
    currentBuilding: '',
    currentStreet: '',
    currentCity: '',
    currentCountry: '',
    currentPostcode: '',
    currentEircode: '',
    // Payment fields
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardName: '',
    billingAddress: ''
  });
  const [useHomeAsCurrentLocation, setUseHomeAsCurrentLocation] = useState(false);
  const [isLoadingEircode, setIsLoadingEircode] = useState(false);
  const [isLoadingLocation, setIsLoadingLocation] = useState(false);
  const [eircodeError, setEircodeError] = useState('');
