                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">Street</label>
                        <input
                          type="text"
                          name="homeStreet"
                          value={formData.homeStreet}
                          onChange={handleInputChange}
                          placeholder="Street name"
                          className="w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                        />
                      </div>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">City</label>
                        <input
                          type="text"
                          name="homeCity"
                          value={formData.homeCity}
                          onChange={handleInputChange}
                          placeholder="City"
                          className="w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">Country</label>
                        <input
                          type="text"
                          name="homeCountry"
                          value={formData.homeCountry}
                          onChange={handleInputChange}
                          placeholder="Country"
                          className="w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                        />
                      </div>
                    </div>
                    {/* <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Postcode</label>
                      <input
                        type="text"
                        name="locationPostcode"
                        value={formData.locationPostcode}
                        onChange={handleInputChange}
                        placeholder="Postcode"
                        className="w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                      />
                    </div> */}

                  </div>
                  <h3 className="text-xl font-semibold text-gray-900 py-4">Current Location</h3>

                  {/* Manual Location Fields */}
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">

                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">Building/Flat</label>
                        <input
                          type="text"
                          name="currentBuilding"
                          value={formData.currentBuilding}
                          onChange={handleInputChange}
                          placeholder="Building number"
                          disabled={useHomeAsCurrentLocation}
                          className={`w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all ${useHomeAsCurrentLocation ? 'bg-gray-100 cursor-not-allowed' : ''}`}
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">Street</label>
                        <input
                          type="text"
                          name="currentStreet"
                          value={formData.currentStreet}
                          onChange={handleInputChange}
                          placeholder="Street name"
                          disabled={useHomeAsCurrentLocation}
                          className={`w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all ${useHomeAsCurrentLocation ? 'bg-gray-100 cursor-not-allowed' : ''}`}
                        />
                      </div>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">City</label>
                        <input
                          type="text"
                          name="currentCity"
                          value={formData.currentCity}
                          onChange={handleInputChange}
                          placeholder="City"
                          disabled={useHomeAsCurrentLocation}
                          className={`w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all ${useHomeAsCurrentLocation ? 'bg-gray-100 cursor-not-allowed' : ''}`}
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">Country</label>
                        <input
                          type="text"
                          name="currentCountry"
                          value={formData.currentCountry}
                          onChange={handleInputChange}
                          placeholder="Country"
                          disabled={useHomeAsCurrentLocation}
                          className={`w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all ${useHomeAsCurrentLocation ? 'bg-gray-100 cursor-not-allowed' : ''}`}
                        />
                      </div>
                    </div>
                    {/* <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">Postcode</label>
                      <input
                        type="text"
                        name="locationPostcode"
                        value={formData.locationPostcode}
                        onChange={handleInputChange}
                        placeholder="Postcode"
                        className="w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                      />
                    </div> */}

                  </div>
                </div>
              </div>

              {/* Continue Button */}
              <div className="p-8 bg-white border-t border-gray-100">
                <button
                  onClick={() => setCurrentStep(2)}
                  className={`w-full ${theme.primarySolid} ${theme.primaryHover} text-white py-4 rounded-lg font-semibold text-lg transition-colors`}
                >
                  Continue to Booking & Payment
                </button>
              </div>
            </div>
          ) : (
            // Step 2: Clinic Selection & Payment
            <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
              <div className="p-8">
                <div className="flex items-center space-x-2 mb-6">
                  <MapPin className={theme.accent} size={20} />
                  <h3 className="text-xl font-semibold text-gray-900">Select Clinic & Time Slot</h3>
                </div>
                <p className="text-gray-600 mb-6">Choose from available clinics nearby</p>

                {/* Clinics Grid - 5 per row */}
                <div className="grid grid-cols-5 gap-3 mb-6">
                  {clinics.map((clinic) => (
                    <div
                      key={clinic.id}
                      onClick={() => handleClinicSelect(clinic)}
                      className={`p-3 border-2 rounded-lg cursor-pointer transition-all hover:shadow-md ${selectedClinic?.id === clinic.id
                        ? `${theme.border} ${theme.accentBg}`
                        : 'border-gray-200 hover:border-gray-300'
                        }`}
                    >
                      <h5 className="font-semibold text-sm text-gray-900 mb-1">{clinic.name}</h5>
                      <div className="text-xs text-gray-600 mb-2">
                        <div className="flex items-center justify-between">
                          <span>{clinic.distance}</span>
                          <div className="flex items-center space-x-1">
                            <Star size={10} className="text-yellow-400 fill-current" />
                            <span>{clinic.rating}</span>
                          </div>
                        </div>
                      </div>
                      <div className="text-right">
                        <span className={`text-sm font-bold ${theme.accent}`}>{clinic.price}</span>
                      </div>
                    </div>
                  ))}
                </div>

                {/* Time Slots */}
                {selectedClinic && (
                  <div className="mb-8">
                    <h4 className="font-semibold text-gray-900 mb-4">Available Time Slots for {selectedClinic.name}:</h4>
                    <div className="grid grid-cols-6 gap-3">
                      {selectedClinic.slots.map((slot, index) => (
                        <button
                          key={index}
                          onClick={() => handleSlotSelect(slot)}
                          className={`p-3 border-2 rounded-lg text-sm font-medium transition-all ${selectedSlot === slot
                            ? `${theme.primarySolid} text-white`
                            : 'border-gray-200 text-gray-700 hover:border-blue-300 hover:bg-blue-50'
                            }`}
                        >
                          {slot}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
                {/* Payment Information */}
                <div className="border-t border-gray-100 pt-8">
                  <div className="flex items-center space-x-2 mb-6">
                    <Users className={theme.accent} size={20} />
                    <h3 className="text-xl font-semibold text-gray-900">Payment Information</h3>
                  </div>

                  <div className="grid md:grid-cols-2 gap-6">
                    <div className="space-y-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">Card Number *</label>
                        <input
                          type="text"
                          name="cardNumber"
                          value={formData.cardNumber}
                          onChange={handleInputChange}
                          placeholder="1234 5678 9012 3456"
                          className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                        />
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2">Expiry Date *</label>
                          <input
                            type="text"
                            name="expiryDate"
                            value={formData.expiryDate}
                            onChange={handleInputChange}
                            placeholder="MM/YY"
                            className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                          />
                        </div>
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-2">CVV *</label>
                          <input
                            type="text"
                            name="cvv"
                            value={formData.cvv}
                            onChange={handleInputChange}
                            placeholder="123"
                            className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                          />
                        </div>
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">Cardholder Name *</label>
                        <input
                          type="text"
                          name="cardName"
                          value={formData.cardName}
                          onChange={handleInputChange}
                          placeholder="John Doe"
                          className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                        />
                      </div>
                      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                        <div className="flex items-start space-x-2">
                          <div className="text-yellow-600 mt-0.5">🔒</div>
                          <div>
                            <p className="text-sm font-medium text-yellow-800">Secure Payment</p>
                            <p className="text-xs text-yellow-700 mt-1">Your payment information is encrypted and secure.</p>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="space-y-4 pt-6">
                      {/* Booking Summary */}
                      {selectedClinic && selectedSlot && (
                        <div className={`p-4 rounded-lg ${theme.accentBg} mb-6`}>
                          <h4 className="font-semibold text-gray-900 mb-2">Booking Summary</h4>
                          <div className="space-y-3 text-sm text-gray-600">
                            <p>🏥 Clinic: {selectedClinic.name}</p>
                            <p>🕐 Time: {selectedSlot}</p>
                            <p>👤 Patient: {formData.fullName || 'Not specified'}</p>
                            <p>📧 Email: {formData.email || 'Not specified'}</p>
                            <p>📞 Phone: {formData.phoneNumber || 'Not specified'}</p>
                          </div>
                          <div className="mt-4 pt-4 border-t border-gray-200 pb-14">
                            <div className="flex justify-between items-center">
                              <span className="font-semibold">Total:</span>
                              <span className={`text-2xl font-bold ${theme.accent}`}>{selectedClinic.price}</span>
                            </div>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="p-8 bg-white border-t border-gray-100">
                <div className="flex space-x-4">
                  <button
                    onClick={() => setCurrentStep(1)}
                    className="flex-1 px-6 py-4 border-2 border-gray-200 text-gray-700 rounded-lg font-semibold hover:bg-gray-50 transition-colors"
                  >
                    Back to Details
                  </button>
                  <button
                    className={`flex-1 ${theme.primarySolid} ${theme.primaryHover} text-white py-4 rounded-lg font-semibold text-lg transition-colors`}
                    disabled={!selectedClinic || !selectedSlot}
                  >
                    Complete Booking & Pay {selectedClinic?.price || ''}
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </section>
    </div>
  );
}
